public class Ship { private int health; private String name; public Ship(int size, String n) { health = size; name = n; } public int getHealth() { return health; } public void setHealth(int h) { health = h; } public String getName() { return name; } public String isSunk() { if (health == 0) { String s; s = "We\'ve sunk the enemy's " + name; return s + "\n Well done captain!\n"; } return ""; } }